function seguent(){
	if($actual.next().hasClass('final')){
		$actual=$($('.inici').next());
	}else{
		$actual=$($actual.next());
	}
	if($actual.hasClass('isotope-hidden')){
		seguent();
	}else{
		obre($actual.attr('id'));
	}
}

function enrera(){
	if($actual.prev().hasClass('inici')){
		$actual=$($('.final').prev());
	}else{
		$actual=$($actual.prev());
	}

	if($actual.hasClass('isotope-hidden')){
		enrera();
	}else{
		obre($actual.attr('id'));
	}
}

